import React, { Component } from 'react'
import {connect} from 'react-redux'
import { Redirect} from 'react-router-dom'
import { loginUserAction } from '../actions/authAction';
import { Link } from "react-router-dom";
 class LoginComponent extends Component {
    constructor(props) {
        super(props);
        this.state= {
          password: "",
          email :"" 
        }
      }
      handleChange = (event) =>{
        const {name , value} = event.target;
        this.setState({
          [name] : value
        })
      }
     handleOnSubmit = (event) =>{
       event.preventDefault();

       const data = { 
         password: this.state.password,
         email: this.state.email
       }

       this.props.loginUser(data);
      //   console.log(this.props.history);
      //   //histry in props is used to redirect
             
      //   // axios.post('http://localhost:5000/login', data)
      //   // .then(response => console.log(response))
      //   // .catch(function (error) {
      //   //   console.log(error);
      //   // });
      // //  this.props.history.push('/comics');
    
     }
      
    render() {
      console.log(this.props.auth.isLoggedIn)
        if(this.props.auth.isLoggedIn)
        {
          return( <Redirect to='/' />)
        }
        return (
            <div id ="header" className="container-fluid ">
        <div className="row">
          <div className="col-10 mx-auto d-flex justify-content-center ">
            
            <form className="form-style " onSubmit ={this.handleOnSubmit}>
              <h1>Log in</h1>
              <div className="form-group">
                <label htmlFor="exampleInputEmail1"><h4>Email :</h4></label>
                <input
                  type="email"
                  className="form-control"
                  id="exampleInputEmail1"
                 name= "email"
                 value={this.state.email}
                  aria-describedby="emailHelp"
                  placeholder="Email"
                  onChange = {this.handleChange}
                />
              </div>
              <div className="form-group">
                <label htmlFor="exampleInputPassword1"><h4>Password:</h4></label>
                <input
                  type="password"
                  className="form-control"
                  name="password"
                  value={this.state.password}
                  id="exampleInputPassword1"
                  onChange = {this.handleChange}
                  placeholder="Password"
                />
              </div>
              <button type="submit" className="btn btn-get-started">
                Submit
              </button>
              <div className="auth">
              <p>new user ? <Link to ="/signup" >Register</Link></p>
             
              </div>
              
            </form>
          </div>
        </div>
      </div>
        )
    }
}
const mapDispatchToProps = (dispatch) =>{
  return{
    loginUser : (data) => dispatch(loginUserAction(data))
  }
}
const mapStateToProps = ({auth}) =>{
  return{
     auth
  };
}
  


export default connect(mapStateToProps, mapDispatchToProps)(LoginComponent);
