import './App.css';
import Navbar from './Components/Navbar';
import {Switch , Route, Redirect} from 'react-router-dom'
import Home from './Components/Home';
import Contact from './Components/Contact';
import About from './Components/About';
import Service from './Components/Service';
import React, { Component } from 'react'
import api from 'marvel-api'
import CryptoJS from 'crypto-js'

// var marvel = new Marvel({ publicKey: "ee182f248ccfa43f509148540e539433", privateKey: "2dafafc5122792c3486bddeb1fe227aab1dd0def"})
//var api = require('marvel-api');

var marvel = api.createClient({
  publicKey: 'ee182f248ccfa43f509148540e539433',
  privateKey: '2dafafc5122792c3486bddeb1fe227aab1dd0def'
});

var ts = new Date().getTime();
  var hash = CryptoJS.MD5(ts + '2dafafc5122792c3486bddeb1fe227aab1dd0def' + 'ee182f248ccfa43f509148540e539433').toString();
 var url = `http://gateway.marvel.com/v1/public/characters/1011334?ts=${ts}&apikey=ee182f248ccfa43f509148540e539433&hash=${hash}`

 class App extends Component {
  constructor(props) {
    super(props);
    this.state={
       url : "",
       currName: ""
    }
  } 
  componentDidMount(){ 
    marvel.characters.findAll()
    .then( (res) => {
        let x =  res.data[0].resourceURI;
        console.log(res);
       this.setState({
         currName : res.data[0].name,
         url : x
       });
       console.log(this.state.currName);
    })
    .fail(console.error)
    .done();
    }
   render() {
       console.log(hash);
       console.log(url);
    return (
      <div className="App">
      <Navbar />
      <h1>{this.state.currName}</h1>
      <a href ={this.state.url}>{this.state.currName}  </a>
      <Switch>
        <Route  path ='/' exact  component={Home} />
        <Route  path ='/about' exact component={About} />
        <Route  path ='/contact' exact component={Contact} />
        <Route  path ='/service' exact component={Service} />
        <Redirect to='/' />
      </Switch>

    </div>
    )
  }
}


