import React, { Component, useImperativeHandle } from 'react'
import {connect} from 'react-redux'
import { getComicsAction } from '../actions/getDataAction'
import DisplayData from './DisplayData';
class ComicsComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            query: ""
        }
    }
    
    componentDidMount(){
        this.props.getComics();
    //    console.log(this.props.comics);
    }

    handleChange = (e) =>{
         this.setState({
             query : e.target.value
         })
    }
    render() {
       const title = 'Marvel Comics';
        return (
            <>
      <section id="header" className=" d-flex align-items-center">
        <div className="container-fluid nav_bg ">
          <div className="row">
            <div className="col-10 mx-auto">
              <h1>{title}</h1> 
            
                    <input class="mr-sm-2" type="search"  onChange = {this.handleChange} placeholder="Search" aria-label="Search" />
                <DisplayData allData={this.props.comics} />
            </div>
          </div>
        </div>
      </section>
    </>
        )
    }
}

const mapStateToProps = (state) =>{
    return{
        comics : state.comics
    }

}
const mapDispatchToProps = (dispatch) =>{
    return{
        getComics :() => dispatch(getComicsAction())
    }
}

export default  connect(mapStateToProps,mapDispatchToProps)(ComicsComponent)
