import React, { Component } from "react";
import { getCharecterByIdAction, isLoadingAction } from "../actions/getDataAction";
import {connect} from 'react-redux'

class CharcterPageComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            charecter : {}
        }
    }
    componentDidMount (){
        const { match: { params } } = this.props;
        this.props.getCharecterById(params.id);
      
    }
    
  render() {
    return (
      <>
        <div className="container-fluid ">
          <div className="row">
            <div className="col-10 mx-auto">
                <h1>hello</h1>
            </div>
          </div>
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) =>{
    return { 
         charecter : state.marvelData.charecters,
          isLoading :  state.marvelData.isLoading
    };
}
const mapDispatchToProps = (dispatch) =>{
  return{
    getCharecterById : () => dispatch(getCharecterByIdAction()),
    isLoading : () => dispatch(isLoadingAction())
  }
}
export default CharcterPageComponent;
