import React, { Component } from "react";

class FavouriteComponet extends Component {
    constructor(props) {
        super(props);
        this.state = {
            favourite:false
        }
        this.handleFavouriteList = this.handleFavouriteList.bind(this);
    }
    
    handleDropdown = () =>{
       const dropdown = document.getElementById("dropdownMenuButton");
       dropdown.style.background= "#a80b0b";
    }
    handleFavouriteList (){
       
        let fav = !this.state.favourite;
        // this.setState({
        //     favourite: fav
        // });
    }
  render() {
    return (
      <>
        <section id="header" className=" d-flex align-items-center">
          <div className="container-fluid nav_bg ">
            <div className="row">
              <div className="col-10 mx-auto  marginPage">
                <div className="row">
                  <div className="col-6">
                    <h1>My Favourite List</h1>
                    {/* <DisplayHomeData
                    allData={marvel.marvelCharecters}
                    reqParams="charecters"
                  /> */}
                  </div>
                  <div className="col-6 text-center">
                    <div className="dropdown">
                      <button
                        className="btn dropdown-toggle btn-dropdown"
                        type="button"
                        id="dropdownMenuButton"
                        data-toggle="dropdown"
                        aria-haspopup="true"
                        aria-expanded="false"
                        onClick={this.handleDropdown}
                      >
                        Favourite
                      </button>
                      <div
                        className="dropdown-menu"
                        aria-labelledby="dropdownMenuButton"
                      >
                        <button className="dropdown-item" onClick={this.handleFavouriteList}>
                          Charecters
                        </button>
                      
                        <button className="dropdown-item" onClick={this.handleFavouriteList} >
                         Comics
                        </button>
                      </div>
                    </div>
                    {/* <DisplayHomeData
                    allData={marvel.marvelComics}
                    reqParams="comics"
                  /> */}
                  </div>
                </div>
                <hr />
              </div>
            </div>
          </div>
        </section>
      </>
    );
  }
}

export default FavouriteComponet;
